mapper.update:
UPDATE warehouse
code = #{code}, name = #{name}, address = #{address}, manager = #{manager}, contact_phone = #{contactPhone}, status = #{status},
WHERE id = #{id}

mapper.select:
SELECT * FROM warehouse WHERE 1 = 1
AND code = #{code}
AND name LIKE CONCAT('%', #{name}, '%')
AND manager LIKE CONCAT('%', #{manager}, '%')
ORDER BY created_at DESC LIMIT #{size} OFFSET #{offset}
